<template>
<div>
    <div class="p-4 bgtt">
      <div class="w-full container mx-auto  sbg">
        <h2 class="text-xl font-semibold">ค้นหาบุคคลที่จะมาทำงานให้คุณ </h2>
        <v-form class="mt-6">
          <v-autocomplete v-model="category"  @input="run()"   dense filled label="ประเภทของงาน" :items="[{id:null,name:'ทั้งหมด'},...$auth.categories]" item-text="name" item-value="id"></v-autocomplete>
          <v-autocomplete   v-model="subcategory" filled dense :items="[{id:null,name:'ทั้งหมด'},...$auth.getSubCategoryByCategory(category)]" item-text="name" item-value="id" label="ประเภทของงาน"></v-autocomplete>
        </v-form>
      </div>
    </div>

  <div class="w-full container mx-auto h-full justify-center items-center sas"  >
    <div class="p-4">
      <v-card outlined class="mt-4" @click="$router.push(`/admin/search/${user.id}/`)" v-for="user,i in data" :key="i">
        <v-card-text>
          <div class="flex items-center">
            <v-avatar size="80" class="border-2 border-blue-500">
              <v-img v-if="user.image_profile" :src="user.image_profile" ></v-img>
              <span v-else>ผู้ใช้</span>
            </v-avatar>
            <div class="ml-4">
              <div>ชื่อ: {{user.first_name}} {{user.last_name}}</div>
              <div>อายุ: {{user.age}} ปี</div>
              <div>ความสามารถ:   <v-chip v-for="skill, k in user.skills" :key="k" small  class="m-1">#{{skill.name}}</v-chip>
              </div>
            </div>
          </div>
        </v-card-text>
      </v-card>




    </div>
  </div>

</div>
</template>

<script>
export default {
    layout: 'admin',
    data: () => ({
        data: [],
      province:'ทั้งหมด',
      category:null,
      subcategory:null,
    }),
    async created() {
        await this.run();
    },
  methods:{
      async run()
      {
        if(this.category == null){
          this.subcategory = null
        }
        let category = (this.category)?`&skill__category=${this.category}`:``
        let subcategory = (this.subcategory)?`&skill=${this.subcategory}`:``

        this.data = await this.$core.getHttp(`/api/account/userprofile/?is_probation=true${category}${subcategory}`)
      }

  }
}
</script>
